import React from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity, Dimensions,
         Image
} from 'react-native';

const { width, height } = Dimensions.get('window');

const Post = ({ }) => {
    return (
        <View style={styles.container}>
            <View style={{ flexDirection: 'row', justifyContent: 'space-between' }}>
                <Text style={styles.posttxt}>Posts</Text>
                <Text style={styles.filtertxt}>Filters</Text>
            </View>
            <View>
            <Image
                    style={styles.postavatar}
                    source={require('../Img/cover.jpg')}
                />

            </View>


            <View style={styles.horizontalLine} />      

        </View>
    )
}

export default Post;


const styles = StyleSheet.create({
    container: {
        flex: 1, backgroundColor: '#2a322a', padding: 15,marginTop:10
    },
    posttxt: {
        marginTop: 10, marginBottom: 0, fontSize: 18, color: '#ffffff', fontWeight: 'bold'
    },
    filtertxt: {
        marginTop: 10, marginBottom: 0, fontSize: 18, color: "#0693E3",
    },
    horizontalLine: {
        width: "100%", height: 0.5, marginTop: 10, backgroundColor: "#E0E0E0",
    },
    postavatar: {
        height: 20, width: 20, borderRadius: 75,
    },
})
