import React, { useState }  from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity, Dimensions } from 'react-native';

const {width, height} = Dimensions.get('window');

const Login = ({navigation }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    return (
        <View style={{flex:1,width:width-40,height:'10%',alignItems:"center",justifyContent:"center",}}>
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Email"
                    placeholderTextColor="#003f5c"
                    onChangeText={(email) => setEmail(email)}
                />
            </View>
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Password"
                    placeholderTextColor="#003f5c"
                    secureTextEntry={true}
                    onChangeText={(password) => setEmail(password)}
                />
            </View>
            <TouchableOpacity style={styles.loginBtn}>
                <Text style={styles.loginText} onPress={() => navigation.navigate('Profile')} >LOGIN</Text>
            </TouchableOpacity>
            <View>
            <TouchableOpacity>
                <Text style={styles.loginTxt}  onPress={() => navigation.navigate('Register')}>Create New Account..</Text>
            </TouchableOpacity>
            </View>

        </View>
    )
}

export default Login;


const styles = StyleSheet.create({
    inputField: {
        backgroundColor: "#FFC0CB",
        borderRadius: 10,
        width: width-60,
        height: '6%',
        marginBottom: 20,
        // alignItems: "center",
    },
    TextInput: {
        height: 50,
        flex: 1,
        padding: 10,
        marginLeft: 20,
    },
    loginBtn: {
        width:width-200,
        borderRadius:10,
        height:40,
        alignItems:"center",
        justifyContent:"center",
        marginTop:40,
        marginLeft:0,
        backgroundColor:"#FF1493",
   },
   loginTxt: {
    marginTop:30,
    marginBottom: 30,
    color:'green'
    
  }
});
