import React, { useState }  from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity, Dimensions,SafeAreaView } from 'react-native';

const {width, height} = Dimensions.get('window');

const Login = ({navigation }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    return (
        <SafeAreaView style={{flex:1,flexDirection:'column',alignItems:"center",justifyContent:"center",backgroundColor:'#2a322a'}}>
        <View >
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Email"
                    placeholderTextColor="#003f5c"
                    onChangeText={(email) => setEmail(email)}
                />
            </View>
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Password"
                    placeholderTextColor="#003f5c"
                    secureTextEntry={true}
                    onChangeText={(password) => setEmail(password)}
                />
            </View>
            {/* <TouchableOpacity style={styles.loginBtn}>
                <Text style={styles.loginText} onPress={() => navigation.navigate('Profile')} >LOGIN</Text>
            </TouchableOpacity> */}
            <View>
            <TouchableOpacity>
                <Text style={styles.loginTxt}  onPress={() => navigation.navigate('Register')}>Create New Account..</Text>
            </TouchableOpacity>
            <TouchableOpacity style={{marginTop:15,backgroundColor:"#0693E3",borderRadius:5}}>
                    <Text style={{alignSelf:'center',padding:6,color:'#ccdeee',fontWeight:'bold',alignSelf: 'center'}}>Login</Text>
                </TouchableOpacity> 
            </View>
        </View>
        </SafeAreaView>
    )
}

export default Login;


const styles = StyleSheet.create({
    inputField: {
        backgroundColor: "#FFF",
        borderRadius: 5,
        width:250,
        height: 40, 
        marginBottom: 20,
        justifyContent:'center',
        
    },
    TextInput: {
        height: 20,
        padding: 0,
        marginLeft:10,
        justifyContent:'flex-start'
    },
    

});
