import React from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity, Dimensions,
         Image
} from 'react-native';
import Ionicons from 'react-native-vector-icons/Ionicons';

const { width, height } = Dimensions.get('window');

const Post = ({ }) => {
    return (
        <View style={styles.container}>
            <View style={{ flexDirection: 'row', justifyContent: 'space-between' }}>
                <Text style={styles.posttxt}>Posts</Text>
                <Text style={styles.filtertxt}>Filters</Text>
            </View>
            
            <View style={styles.posalign}>
                <Image
                    style={styles.postavatar}
                    source={require('../Img/cover.jpg')}
                />
                <Text style={styles.postxt}>What's on your mind?</Text>
            </View>
            <TouchableOpacity style={styles.touchboxalign}>
            <Ionicons name='chatbox-ellipses' size={20} style={styles.touchboxicon} />
                <Text style={styles.touchboxtxt}>Manage Posts</Text>
            </TouchableOpacity> 
            <View style={styles.horizontalLine} />      
           
        </View>
    )
}

export default Post;


const styles = StyleSheet.create({
    
    container: {
        flex: 1, backgroundColor: '#2a322a', padding: 15,marginTop:10
    },
    posttxt: {
        marginTop: 10, marginBottom: 0, fontSize: 18, color: '#ffffff', fontWeight: 'bold'
    },
    filtertxt: {
        marginTop: 10, marginBottom: 0, fontSize: 18, color: "#0693E3",
    },
    horizontalLine: {
        width: "100%", height: 0.5, marginTop: 10, backgroundColor: "#E0E0E0",
    },
    postavatar: {
        height: 30, width: 30, borderRadius: 75,
    },
    posalign:{ flexDirection: 'row',marginTop: 10 
    },
    postxt:{ marginLeft: 10, fontSize: 14, color: 'white', alignSelf:'center' 
    },
    touchboxalign:{ marginTop:15,backgroundColor:"#555555",borderRadius:5,flexDirection: 'row',justifyContent:'center'
    },
    touchboxicon:{ color: '#ffffff',alignSelf: 'center', 
    },
    touchboxtxt:{padding:6,color:'#FFFFFF',fontWeight:'bold',alignSelf: 'center',
    },
})
