import React from 'react';
import {
    View, Text, StyleSheet, Dimensions, ScrollView, SafeAreaView, Image, ImageBackground,
    TouchableOpacity, TouchableWithoutFeedback
} from 'react-native';
import Ionicons from 'react-native-vector-icons/Ionicons';


const { width, height } = Dimensions.get('window');


const Profile = () => {
    return (
        <View style={styles.container}>
            <View >
                <Image
                    style={styles.coverImg}
                    source={require('../Img/cover.jpg')}
                />
                <View style={styles.profilemount}>
                    <Image
                        style={styles.profileImg}
                        source={require('../Img/panther.jpg')}
                    //   source={{uri:'https://lh5.googleusercontent.com/-b0PKyNuQv5s/AAAAAAAAAAI/AAAAAAAAAAA/AMZuuclxAM4M1SCBGAO7Rp-QP6zgBEUkOQ/s96-c/photo.jpg' }}
                    />
                </View>
            </View>
            <View>
                <Text style={{ alignSelf: 'center', marginTop: 110, fontSize: 20, fontWeight: 'bold',color:'white' }}> Profile Name </Text>
            </View>
            <View style={styles.protouch}>
                <TouchableOpacity style={styles.probtn}>
                    <Ionicons name='add-circle-outline' size={13} style={{ color: 'white' }}> Add to Story</Ionicons>
                </TouchableOpacity>
                <TouchableOpacity style={styles.probtn1}>
                    <Ionicons name='pencil-outline' size={13} style={{ color: 'white' }}> Edit Profile</Ionicons>
                </TouchableOpacity>
                <TouchableOpacity style={styles.probtn2}>
                    <Ionicons name='ellipsis-horizontal-outline' size={13} style={{ color: 'white' }} />
                </TouchableOpacity>
            </View>
            <View style={styles.horizontalLine} />
            
        </View>
    )
}

export default Profile;

const styles = StyleSheet.create({
    container: {
        flex: 1, backgroundColor: '#2a322a',  padding: 20,
    },
    coverImg: {
        height: 150,width: '100%',borderRadius: 5, // position:'absolute',
    },
    profilemount: {
        height: 150, width: 150, borderRadius: 75, backgroundColor: 'red', position: 'absolute',
        alignSelf: 'center', marginTop:'30%'
    },
    profileImg: {
        height: 150, width: 150, borderRadius: 75, backgroundColor: 'red',
    },
    protouch:{ justifyContent: "space-between", flexDirection: "row", marginTop: 10, 
    },
    probtn: {
        alignItems: "center", backgroundColor: "#0693E3", alignSelf: 'center',
        padding: 12, width: '38%', borderRadius: 5
    },
    probtn1: {
        alignItems: "center", backgroundColor: "#555555", alignSelf: 'center',
        padding: 12, width: '38%', borderRadius: 5
    },
    probtn2: {
        alignItems: "center", backgroundColor: "#555555", alignSelf: 'center',
        padding: 12, width: '18%', borderRadius: 5
    },
    horizontalLine: {
        width: "100%", height: 1, marginTop: 10, backgroundColor: "#E0E0E0",
    },

});
