import React, { useState }  from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity, Dimensions,SafeAreaView } from 'react-native';

const {width, height} = Dimensions.get('window');

const Login = ({navigation }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    return (
        <SafeAreaView style={{flex:1,flexDirection:'column',alignItems:"center",justifyContent:"center",backgroundColor:'#2a322a'}}>
        <View >
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Email"
                    placeholderTextColor="#003f5c"
                    onChangeText={(email) => setEmail(email)}
                />
            </View>
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Password"
                    placeholderTextColor="#003f5c"
                    secureTextEntry={true}
                    onChangeText={(password) => setEmail(password)}
                />
            </View>
            <TouchableOpacity style={styles.loginBtn}>
                <Text style={styles.loginText} onPress={() => navigation.navigate('Profile')} >LOGIN</Text>
            </TouchableOpacity>
            <View>
            <TouchableOpacity>
                <Text style={styles.loginTxt}  onPress={() => navigation.navigate('Register')}>Create New Account..</Text>
            </TouchableOpacity>
            </View>
        </View>
        </SafeAreaView>
    )
}

export default Login;


const styles = StyleSheet.create({
    inputField: {
        backgroundColor: "#FFF",
        borderRadius: 10,
        width:250,
        height: 50, 
        marginBottom: 20,
        justifyContent:'center',
        
    },
    TextInput: {
        height: 20,
        padding: 0,
        marginLeft:10,
        justifyContent:'flex-start'
    },
    loginBtn: {
        width:100,
        borderRadius:10,
        height:40,
        alignItems:"center",
        justifyContent:"center",
        alignSelf:'center',
        marginTop:40,
        marginLeft:0,
        backgroundColor:"#FF1493",
   },
   loginTxt: {
        marginTop:30,
        marginBottom: 30,
        color:'green',
        alignSelf:'center',
        justifyContent:"center",
        
  }
});
