import React, { useState } from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity, Dimensions } from 'react-native';

const { width, height } = Dimensions.get('window');

const Register = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    return (
        <View style={{ flex: 1, width: width - 40, height: '10%', alignItems: "center", justifyContent: "center", }}>
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Name"
                    placeholderTextColor="#003f5c"
                    onChangeText={(email) => setEmail(email)}
                />
            </View>
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Mobile No"
                    placeholderTextColor="#003f5c"
                    onChangeText={(email) => setEmail(email)}
                />
            </View>
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Email."
                    placeholderTextColor="#003f5c"
                    onChangeText={(email) => setEmail(email)}
                />
            </View>
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="PAN No"
                    placeholderTextColor="#003f5c"
                    onChangeText={(email) => setEmail(email)}
                />
            </View>
            <View style={styles.inputField}>
                <TextInput
                    style={styles.TextInput}
                    placeholder="Password"
                    placeholderTextColor="#003f5c"
                    secureTextEntry={true}
                    onChangeText={(password) => setEmail(password)}
                />
            </View>
            <View>

            <TouchableOpacity style={styles.RegtrBtn}>
                <Text  onPress={() => navigation.navigate('Profile')}>REGISTER</Text>
            </TouchableOpacity>
            </View>

            <View>
            <TouchableOpacity>
                <Text style={styles.loginTxt}  onPress={() => navigation.push('Login')}>already register login here</Text>
            </TouchableOpacity>
            </View>

        </View>
    )
}

export default Register;


const styles = StyleSheet.create({
    inputField: {
        backgroundColor: "#FFC0CB",
        borderRadius: 10,
        width: width - 60,
        height: '6%',
        marginBottom: 20,
        // alignItems: "center",
    },
    TextInput: {
        height: 50,
        flex: 1,
        padding: 10,
        marginLeft: 20,
    },
    RegtrBtn: {
        width: width - 200,
        borderRadius: 10,
        height: 40,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 40,
        marginLeft: 0,
        backgroundColor: "#FF1493",
    },
    loginTxt: {
        marginTop:30,
        marginBottom: 30,
        color:'green'
        
      }
});
