import React from 'react';
import{ View,Text,StyleSheet,Dimensions,ScrollView, SafeAreaView,Image,ImageBackground  } from 'react-native';


const {width, height} = Dimensions.get('window');


const Profile=()=>{
    return(
        <SafeAreaView style={{flex: 1, backgroundColor: '#fff'}}>
            <ScrollView style={styles.container}>
            <View >
            <Image  
                  style={styles.coverImg} 
                  source={{uri:'https://lh5.googleusercontent.com/-b0PKyNuQv5s/AAAAAAAAAAI/AAAAAAAAAAA/AMZuuclxAM4M1SCBGAO7Rp-QP6zgBEUkOQ/s96-c/photo.jpg' }}

               />      
               {/* <View style={styles.profilemt}>
               <Image 
                  style={styles.profileImg} 
                //   source={{uri:'https://lh5.googleusercontent.com/-b0PKyNuQv5s/AAAAAAAAAAI/AAAAAAAAAAA/AMZuuclxAM4M1SCBGAO7Rp-QP6zgBEUkOQ/s96-c/photo.jpg' }}

               />
               </View>          */}
            </View>
            
               

            </ScrollView>
        </SafeAreaView>
    )
}

export default Profile;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        padding: 20,
      },
      coverImg: {
        height: 150,
        width: width-40,
        borderRadius: 5,
        // position:'absolute',
      },
      profilemt:{
        alignItems: "center",
        justifyContent: "center",
        justifyContent:'flex-end',
        position:'absolute',
        alignSelf:'center',
        marginTop:100
      },
      profileImg: {
        height: 150,
        width: 150,
        borderRadius: 75,
        backgroundColor:'red'
        
        

    },
      
  });
